  axis(at = pretty(dataframe[[y1]]), side = 2,lwd=1.5)
  title(main=title, line=1, cex.lab=1.2,mgp=c(1,4,0))
  par(new = TRUE,mgp=c(0,1, -0.75),col.axis=2 )
  plot(dataframe[[x]], dataframe[[y2]], axes = FALSE, col = 2, xlab = "", ylab = "",col.lab=2,
